#include "gtest/gtest.h"
#include "./../../big_integer.h"

class SetupBigIntegersExponentiation: public testing::Test
{
protected:
	std::string big_number1{"91321378118309180938109380983109"};
	std::string big_number2{"902342349278113243248309180938234234109380983109572387399309213"};
	int exponent1{12};
	int exponent2{17};
	std::string big_number1_to_the_exponent1{""};
	std::string big_number1_to_the_exponent2{""};
	std::string big_number2_to_the_exponent1{""};
	std::string big_number2_to_the_exponent2{""};
	BigInteger<4000> big_int1 = BigInteger<4000>(big_number1);
	BigInteger<4000> big_int2 = BigInteger<4000>(big_number2);
};

TEST_F(SetupBigIntegersExponentiation, test_multiplication_number1_exponents)
{
	auto result = big_int1.to_power(exponent1);
	EXPECT_TRUE(result.to_number_string() == big_number1_to_the_exponent1);
	result = big_int1.to_power(exponent2);
	EXPECT_TRUE(result.to_number_string() == big_number1_to_the_exponent2);
}


TEST_F(SetupBigIntegersExponentiation, test_multiplication_number2_exponents)
{
	auto result = big_int2.to_power(exponent1);
	EXPECT_TRUE(result.to_number_string() == big_number2_to_the_exponent1);
	result = big_int2.to_power(exponent2);
	EXPECT_TRUE(result.to_number_string() == big_number2_to_the_exponent2);
}
